import React from 'react';
import styled from '@emotion/styled';

const ContenedorFrase = styled.div `
padding: 3 rem;
border-radius: .5rem;
background-color: #fff;
max-width: 800px;
margin-top: rem;
`;

const Frase = ({frase}) => {
    return ( 
        <ContenedorFrase>
         <h1> {frase.quote} </h1>
        <p> {frase.author} </p>
        </ContenedorFrase>
        );
}
 
export default Frase;
